import {
  FC,
  ReactNode,
  useCallback,
  useEffect,
  useMemo,
  useState,
} from "react";
import { WalletContext } from "./useWallet";
import type {
  AccountInfo,
  NetworkInfo,
  SignMessagePayload,
  Wallet,
  WalletInfo,
  WalletName,
} from "@aptos-labs/wallet-adapter-core";
import { WalletCore } from "@aptos-labs/wallet-adapter-core";

import { TxnBuilderTypes, Types } from "aptos";

export interface AptosWalletProviderProps {
  children: ReactNode;
  plugins: ReadonlyArray<Wallet>;
  autoConnect?: boolean;
}

const initialState: {
  account: AccountInfo | null;
  network: NetworkInfo | null;
  connected: boolean;
  wallet: WalletInfo | null;
} = {
  connected: false,
  account: null,
  network: null,
  wallet: null,
};

export const AptosWalletAdapterProvider: FC<AptosWalletProviderProps> = ({
  children,
  plugins,
  autoConnect = false,
}: AptosWalletProviderProps) => {
  const [{ connected, account, network, wallet }, setState] =
    useState(initialState);

const [isLoading, setIsLoading] = useState<boolean>(true);

const walletCore = useMemo(() => new WalletCore(plugins), []);
const [wallets, setWallets] = useState<ReadonlyArray<Wallet>>(walletCore.wallets);

const connect = (walletName: WalletName) => {
  try {
    setIsLoading(true);
    walletCore.connect(walletName);
  } catch (e) {
    console.log("connect error", e);
  } finally {
    setIsLoading(false);
  }
};

const disconnect = () => {
  try {
    walletCore.disconnect();
  } catch (e) {
    console.log("disconnect error", e);
  }
};

const signAndSubmitTransaction = async (
  transaction: Types.TransactionPayload
) => {
  try {
    return await walletCore.signAndSubmitTransaction(transaction);
  } catch (error: any) {
    throw error;
  }
};

const signAndSubmitBCSTransaction = async (
  transaction: TxnBuilderTypes.TransactionPayload
) => {
  try {
    return await walletCore.signAndSubmitBCSTransaction(transaction);
  } catch (error: any) {

    throw error;
  }
};

const signTransaction = async (transaction: Types.TransactionPayload) => {
  try {
    return await walletCore.signTransaction(transaction);
  } catch (error: any) {
    throw error;
  }
};

const signMessage = async (message: SignMessagePayload) => {
  try {
    return await walletCore.signMessage(message);
  } catch (error: any) {
    throw error;
  }
};

const signMessageAndVerify = async (message: SignMessagePayload) => {
  try {
    return await walletCore.signMessageAndVerify(message);
  } catch (error: any) {
    throw error;
  }
};

useEffect(() => {
  if (autoConnect) {
    if (localStorage.getItem("AptosWalletName")) {
      connect(localStorage.getItem("AptosWalletName") as WalletName);
    }
  }
}, wallets);

useEffect(() => {
  if (connected) {
    walletCore.onAccountChange();
    walletCore.onNetworkChange();
  }
}, [...wallets, connected]);

// Handle the adapter's connect event
const handleConnect = () => {

  setState((state) => {
    return {
      ...state,
      connected: true,
      account: walletCore.account,
      network: walletCore.network,
      wallet: walletCore.wallet,
    };
  });
};

// Handle the adapter's disconnect event
const handleDisconnect = () => {
  if (!connected) return;
  setState((state) => {
    return {
      ...state,
      connected: false,
      account: walletCore.account,
      network: walletCore.network,
      wallet: null,
    };
  });
};

// Handle the adapter's account change event
const handleAccountChange = useCallback(() => {
  if (!connected) return;
  if (!walletCore.wallet) return;
  setState((state) => {
    return {
      ...state,
      account: walletCore.account,
    };
  });
}, [connected]);

// Handle the adapter's network event
const handleNetworkChange = useCallback(() => {
  if (!connected) return;
  if (!walletCore.wallet) return;
  setState((state) => {
    return {
      ...state,

      network: walletCore.network,
    };
  });
}, [connected]);

// Whenever the readyState of any supported wallet changes we produce a new wallets state array
// which in turn causes consumers of the `useWallet` hook to re-render.
// See https://github.com/aptos-labs/aptos-wallet-adapter/pull/129#issuecomment-1519026572 for reasoning.
const handleReadyStateChange = (wallet: Wallet) => {
  setWallets((wallets) => [...wallets]);
};

useEffect(() => {
  walletCore.on("connect", handleConnect);
  walletCore.on("disconnect", handleDisconnect);
  walletCore.on("accountChange", handleAccountChange);
  walletCore.on("networkChange", handleNetworkChange);
  walletCore.on("readyStateChange", handleReadyStateChange);
  return () => {
    walletCore.off("connect", handleConnect);
    walletCore.off("disconnect", handleDisconnect);
    walletCore.off("accountChange", handleAccountChange);
    walletCore.off("networkChange", handleNetworkChange);

      walletCore.off("readyStateChange", handleReadyStateChange);
    };
  }, [...wallets, connected]);

  return (
    <WalletContext.Provider
      value={{
        connect,
        account,
        network,
        connected,
        disconnect,
        wallet,
        wallets,
        signAndSubmitTransaction,
        signAndSubmitBCSTransaction,
        signTransaction,
        signMessage,
        signMessageAndVerify,
        isLoading,
      }}
    >
      {children}
    </WalletContext.Provider>
  );
};

import * as React from "react";
export {
  useWallet,
  WalletReadyState,
  NetworkName,
  isInAppBrowser,
  isMobile,
  isRedirectable,
} from "./useWallet";
export type { WalletName, Wallet } from "./useWallet";
export * from "./WalletProvider";

import {
  AccountInfo,
  NetworkInfo,
  WalletInfo,
  WalletName,
  SignMessagePayload,
  SignMessageResponse,
  Wallet,
  WalletReadyState,
  NetworkName,
  isInAppBrowser,
  isRedirectable,
  isMobile,
} from "@aptos-labs/wallet-adapter-core";
import { createContext, useContext } from "react";
import { TxnBuilderTypes, Types } from "aptos";

export type { WalletName, Wallet };
export {
  WalletReadyState,
  NetworkName,
  isInAppBrowser,
  isRedirectable,
  isMobile,
};

export interface WalletContextState {
  connected: boolean;
  isLoading: boolean;
  account: AccountInfo | null;
  network: NetworkInfo | null;
  connect(walletName: WalletName): void;
  disconnect(): void;
  wallet: WalletInfo | null;
  wallets: ReadonlyArray<Wallet>;
  signAndSubmitTransaction<T extends Types.TransactionPayload, V>(
    transaction: T,
    options?: V
  ): Promise<any>;
  signAndSubmitBCSTransaction<T extends TxnBuilderTypes.TransactionPayload, V>(
    transaction: T,
    options?: V
  ): Promise<any>;

  signTransaction<T extends Types.TransactionPayload, V>(
    transaction: T,
    options?: V
  ): Promise<any>;
  signMessage(message: SignMessagePayload): Promise<SignMessageResponse | null>;
  signMessageAndVerify(message: SignMessagePayload): Promise<boolean>;
}

const DEFAULT_COUNTEXT = {
  connected: false,
};

export const WalletContext = createContext<WalletContextState>(
  DEFAULT_COUNTEXT as WalletContextState
);

export function useWallet(): WalletContextState {
  const context = useContext(WalletContext);
  if (!context) {
    throw new Error("useWallet must be used within a WalletContextState");
  }
  return context;
}

