import {
  AccountInfo,
  AdapterPlugin,
  NetworkInfo,
  SignMessagePayload,
  SignMessageResponse,
  WalletInfo,
  WalletReadyState,
} from '@aptos-labs/wallet-adapter-core';
import { TxnBuilderTypes, Types } from 'aptos';
import { Extension } from '@magic-sdk/commons';
import { InstanceWithExtensions, SDKBase } from '@magic-sdk/provider';
import { AptosExtension } from '.';
import { APTOS_NETWORKS, APTOS_NODE_URLS, APTOS_WALLET_NAME, ICON_BASE64 } from './constants';
import { MagicAptosWalletConfig } from './type';

export class MagicAptosWallet implements AdapterPlugin {
  readonly name = APTOS_WALLET_NAME;
  readonly url = 'https://magic.link/';
  readonly icon = ICON_BASE64;

  readonly providerName = 'magicWalletMA';

  provider: InstanceWithExtensions<SDKBase, [AptosExtension, Extension]> | undefined;
  config?: MagicAptosWalletConfig;

  readyState?: WalletReadyState = WalletReadyState.Loadable;

  private accountInfo: AccountInfo | null;

  constructor(

  magic: InstanceWithExtensions<SDKBase, [AptosExtension, Extension]> | undefined,
  magicAptosWalletConfig?: MagicAptosWalletConfig,
) {
  this.provider = magic;
  this.accountInfo = null;
  this.config = magicAptosWalletConfig;
}

async connect(): Promise<AccountInfo> {
  if (!this.provider) {
    throw new Error('Provider is not defined');
  }

  const isLoggedIn = await this.provider.user.isLoggedIn();
  if (isLoggedIn) {
    const accountInfo = await this.account();
    return accountInfo;
  }

  if (!this.config) {
    throw new Error('Please set connect config first');
  }

  return this.config.connect();
}

async account(): Promise<AccountInfo> {
  if (!this.provider) {
    throw new Error('Provider is not defined');
  }

  if (!this.accountInfo) {
    const accountInfo = await this.provider.aptos.getAccountInfo();
    this.accountInfo = accountInfo;
  }

  return this.accountInfo;
}

async disconnect(): Promise<void> {
  if (!this.provider) {

    throw new Error('Provider is not defined');
  }

  this.accountInfo = null;
  await this.provider.user.logout();
}

async signTransaction(transaction: Types.TransactionPayload): Promise<Uint8Array> {
  if (!this.provider) {
    throw new Error('Provider is not defined');
  }

  const accountInfo = await this.account();
  return this.provider.aptos.signTransaction(accountInfo.address, transaction);
}

async signAndSubmitTransaction(
  transaction: Types.TransactionPayload,
  options?: any,
): Promise<{ hash: Types.HexEncodedBytes }> {
  if (!this.provider) {
    throw new Error('Provider is not defined');
  }

  const accountInfo = await this.account();
  return this.provider.aptos.signAndSubmitTransaction(accountInfo.address, transaction, options);
}

async signAndSubmitBCSTransaction(transaction: TxnBuilderTypes.TransactionPayload): Promise<any> {
  if (!this.provider) {
    throw new Error('Provider is not defined');
  }

  const accountInfo = await this.account();
  return this.provider.aptos.signAndSubmitBCSTransaction(accountInfo.address, transaction);
}

async signMessage(message: SignMessagePayload): Promise<SignMessageResponse> {
  if (!this.provider) {
    throw new Error('Provider is not defined');
  }

  const accountInfo = await this.account();
  return this.provider.aptos.signMessage(accountInfo.address, message);
}

async signMessageAndVerify(message: SignMessagePayload): Promise<boolean> {
  if (!this.provider) {
    throw new Error('Provider is not defined');
  }

  const accountInfo = await this.account();
  return this.provider.aptos.signMessageAndVerify(accountInfo.address, message);
}

async network(): Promise<NetworkInfo> {
  if (!this.provider) {
    throw new Error('Provider is not defined');
  }

  const { nodeUrl } = this.provider.aptos.aptosConfig;

  if (nodeUrl.includes(APTOS_NODE_URLS.MAINNET)) {
    return APTOS_NETWORKS[APTOS_NODE_URLS.MAINNET];
  }

    if (nodeUrl.includes(APTOS_NODE_URLS.TESTNET)) {
      return APTOS_NETWORKS[APTOS_NODE_URLS.TESTNET];
    }

    if (nodeUrl.includes(APTOS_NODE_URLS.DEVNET)) {
      return APTOS_NETWORKS[APTOS_NODE_URLS.DEVNET];
    }

    throw new Error('Invalid node url');
  }

  wallet(): WalletInfo {
    return {
      name: this.name,
      url: this.url,
      icon: this.icon,
    };
  }

  async onNetworkChange(callback: any): Promise<void> {
    console.warn('onNetworkChange is not supported');
    callback();
    return Promise.resolve();
  }

  async onAccountChange(callback: any): Promise<void> {
    console.warn('onAccountChange is not supported');
    callback();
    return Promise.resolve();
  }
}

import { NetworkName, WalletName } from '@aptos-labs/wallet-adapter-core';

export const APTOS_WALLET_NAME = 'Magic' as WalletName<'Magic'>;

export const APTOS_NODE_URLS = {
  MAINNET: 'https://fullnode.mainnet.aptoslabs.com',
  TESTNET: 'https://fullnode.testnet.aptoslabs.com',
  DEVNET: 'https://fullnode.devnet.aptoslabs.com',
};

export const APTOS_NETWORKS = {
  [APTOS_NODE_URLS.MAINNET]: {
    name: NetworkName.Mainnet,
    chainId: '1',
    url: APTOS_NODE_URLS.MAINNET,
  },
  [APTOS_NODE_URLS.TESTNET]: {
    name: NetworkName.Testnet,
    chainId: '2',
    url: APTOS_NODE_URLS.TESTNET,
  },
  [APTOS_NODE_URLS.DEVNET]: {
    name: NetworkName.Devnet,
    url: APTOS_NODE_URLS.DEVNET,
  },
};

export const ICON_BASE64 =

export const APTOS_PAYLOAD_TYPE = {
  ENTRY_FUCNTION_PAYLOAD: 'entry_function_payload',
  SCRIPT_PAYLOD: 'script_payload',
  MODULE_BUNDLE_PAYLOAD: 'module_bundle_payload',
  MULTISIG_PAYLOD: 'multisig_payload',
} as const;

import { Extension } from '@magic-sdk/commons';

// eslint-disable-next-line @typescript-eslint/ban-ts-comment
// @ts-ignore
import { AptosClient, BCS, TxnBuilderTypes, Types, getAddressFromAccountOrAddress } from 'aptos';
import { AccountInfo, SignMessagePayload, SignMessageResponse } from '@aptos-labs/wallet-adapter-core';
import { AptosConfig, AptosPayloadMethod } from './type';
import { APTOS_PAYLOAD_TYPE } from './constants';

export { MagicAptosWallet } from './MagicAptosWallet';
export class AptosExtension extends Extension.Internal<'aptos', any> {
  name = 'aptos' as const;
  config: any = {};

  constructor(public aptosConfig: AptosConfig) {
    super();

    this.config = {
      rpcUrl: '',
      chainType: 'APTOS',
      options: {
        nodeUrl: aptosConfig.nodeUrl,
      },
    };
  }

  private serializeRawTransaction = (rawTransaction: TxnBuilderTypes.RawTransaction) => {
    const s = new BCS.Serializer();
    rawTransaction.serialize(s);
    return s.getBytes();
  };

private generateRawTransactionWihtTypedPayload = async (
  address: string,
  transaction: Types.TransactionPayload,
  options?: any,
) => {
  const client = new AptosClient(this.config.options.nodeUrl);

  if (transaction.type === APTOS_PAYLOAD_TYPE.ENTRY_FUCNTION_PAYLOAD) {
    const rawTransaction = await client.generateTransaction(
      address,
      transaction as Types.EntryFunctionPayload,
      options,
    );
    return rawTransaction;
  }

  throw new Error(`[${transaction.type}] is not supported`);
};

getAccount = () => {
  return this.request<string>(this.utils.createJsonRpcRequestPayload(AptosPayloadMethod.AptosGetAccount, []));
};

signTransaction = async (address: string, transaction: Types.TransactionPayload) => {
  const rawTransaction = await this.generateRawTransactionWihtTypedPayload(address, transaction);
  const transactionBytes = this.serializeRawTransaction(rawTransaction);

  return this.request<Uint8Array>(

    this.utils.createJsonRpcRequestPayload(AptosPayloadMethod.AptosSignTransaction, [
      {
        address,
        transactionBytes,
      },
    ]),
  );
};

getAccountInfo = async (): Promise<AccountInfo> => {
  const address = await this.request<string>(
    this.utils.createJsonRpcRequestPayload(AptosPayloadMethod.AptosGetAccount, []),
  );

  return this.request<AccountInfo>(
    this.utils.createJsonRpcRequestPayload(AptosPayloadMethod.AptosGetAccountInfo, [{ address }]),
  );
};

signAndSubmitTransaction = async (
  address: string,
  transaction: Types.TransactionPayload,
  options?: any,
): Promise<{ hash: Types.HexEncodedBytes }> => {
  const rawTransaction = await this.generateRawTransactionWihtTypedPayload(address, transaction, options);
  const transactionBytes = this.serializeRawTransaction(rawTransaction);

  return this.request<{ hash: Types.HexEncodedBytes }>(

    this.utils.createJsonRpcRequestPayload(AptosPayloadMethod.AptosSignAndSubmitTransaction, [
      {
        address,
        transactionBytes,
      },
    ]),
  );
};

signAndSubmitBCSTransaction = async (address: string, transaction: TxnBuilderTypes.TransactionPayload) => {
  const client = new AptosClient(this.config.options.nodeUrl);
  const addressHex = getAddressFromAccountOrAddress(address);

  const rawTransaction = await client.generateRawTransaction(addressHex, transaction);
  const transactionBytes = this.serializeRawTransaction(rawTransaction);

  return this.request<{ hash: Types.HexEncodedBytes }>(
    this.utils.createJsonRpcRequestPayload(AptosPayloadMethod.AptosSignAndSubmitBCSTransaction, [
      {
        address,
        transactionBytes,
      },
    ]),
  );
};

signMessage = async (address: string, message: SignMessagePayload): Promise<SignMessageResponse> => {
  return this.request<SignMessageResponse>(

      this.utils.createJsonRpcRequestPayload(AptosPayloadMethod.AptosSignMessage, [
        {
          address,
          message,
        },
      ]),
    );
  };

  signMessageAndVerify = async (address: string, message: SignMessagePayload): Promise<boolean> => {
    return this.request<boolean>(
      this.utils.createJsonRpcRequestPayload(AptosPayloadMethod.AptosSignMessageAndVerify, [
        {
          address,
          message,
        },
      ]),
    );
  };
}

