import { Signer } from './SignerInterface';
import { TaquitoPayloadMethod } from './type';

export class MagicSigner implements Signer {
  private magic: any;
  constructor(private pkh: string, private pK: string, magic: any) {
    this.magic = magic;
  }

  async publicKeyHash(): Promise<string> {
    return this.pkh;
  }

  async publicKey(): Promise<string> {
    return this.pK;
  }

  async secretKey(): Promise<string> {
    throw new Error('Secret key cannot be exposed');
  }

  async sign(
    bytes: string,
    watermark?: Uint8Array,
  ): Promise<{
    bytes: string;
    sig: string;
    prefixSig: string;
    sbytes: string;
  }> {
    return this.magic.request(
      this.magic.utils.createJsonRpcRequestPayload(TaquitoPayloadMethod.TaquitoSign, [
        {
          bytes,
          watermark,
        },
      ]),
    );
  }
}

/**
 * @description Signer interface which is used across taquito in order to sign and inject operation
 */
export interface Signer {
  /**
   *
   * @param op Operation to sign
   * @param magicByte Magic bytes 1 for block, 2 for endorsement, 3 for generic, 5 for the PACK format of michelson
   */
  sign(
    op: string,
    magicByte?: Uint8Array,
  ): Promise<{
    bytes: string;
    sig: string;
    prefixSig: string;
    sbytes: string;
  }>;
  /**
   * @description Return the public key of the account used by the signer
   */
  publicKey(): Promise<string>;

  /**
   * @description Return the public key hash of the account used by the signer
   */
  publicKeyHash(): Promise<string>;

  /**
   * @description Optionally return the secret key of the account used by the signer
   */
  secretKey(): Promise<string | undefined>;
}

