import { getScriptData } from '../utils/script-data';
import { createMagicInstance } from '../utils/magic-instance';

export async function login(): Promise<void> {
  const {
    src,
    apiKey,
    locale,
    redirectURI = `${window.location.origin}/callback`,
    termsOfServiceURI,
    privacyPolicyURI,
    debug,
  } = getScriptData();

  const magic = createMagicInstance(apiKey, src.origin, locale);

  async function handleOAuthLogin(provider: any) {
    await magic.pnp.saveLastUsedProvider(provider);
    await magic.oauth.loginWithRedirect({ provider, redirectURI });
  }

  async function handleEmailLinkLogin(email: any) {
    await magic.pnp.saveLastUsedProvider('email_link');
    const didt = await magic.auth.loginWithMagicLink({ email, redirectURI });
    window.location.href = `${redirectURI}?didt=${encodeURIComponent(didt!)}`;
  }

  async function handleSMSLogin(phoneNumber: any) {
    await magic.pnp.saveLastUsedProvider('sms');

    const didt = await magic.auth.loginWithSMS({ phoneNumber });
    window.location.href = `${redirectURI}?didt=${encodeURIComponent(didt!)}`;
  }

  const [loginType, arg] = await magic.pnp.getLoginMethod({ debug, termsOfServiceURI, privacyPolicyURI });

  switch (loginType) {
    case 'oauth2':
      return handleOAuthLogin(arg).catch(login);

    case 'email_link':
      return handleEmailLinkLogin(arg).catch(login);

    case 'sms':
      return handleSMSLogin(arg).catch(login);

    default:
      return login();
  }
}

import { getScriptData } from '../utils/script-data';
import { createMagicInstance } from '../utils/magic-instance';

export async function logout(): Promise<void> {
  // In this context, `loginURI` and `redirectURI` are the same.
  // We simply need a location to redirect to after attempting to logout.
  const { src, apiKey, locale, loginURI, redirectURI = window.location.origin } = getScriptData();
  const magic = createMagicInstance(apiKey, src.origin, locale);
  await magic.user.logout().catch(() => {});
  window.location.href = loginURI || redirectURI;
}

import { getScriptData } from '../utils/script-data';
import { createMagicInstance } from '../utils/magic-instance';

export async function settings(): Promise<void> {
  // In this context, `loginURI` and `redirectURI` have distinct purposes.
  // `loginURI` is the location we redirect to when calling into settings fails.
  // `redirectURI` is the location we redirect to after the user has dismissed the settings page.
  const {
    src,
    apiKey,
    locale,
    redirectURI = `${window.location.origin}/callback`,
    loginURI = window.location.origin,
  } = getScriptData();

  const magic = createMagicInstance(apiKey, src.origin, locale);

  try {
    const prevUserMetadata = magic.pnp.encodeUserMetadata(await magic.user.getMetadata());
    const currUserMetadata = magic.pnp.encodeUserMetadata(await magic.pnp.showSettings());
    window.location.href = `${redirectURI}?prev_user_metadata=${prevUserMetadata}&curr_user_metadata=${currUserMetadata}`;
  } catch {
    window.location.href = loginURI;
  }
}

