/**

 * Checks if the given `ext` is compatible with the platform & version of Magic
 * SDK currently in use.
 */
function checkExtensionCompat(ext: Extension<string>) {
  if (ext.compat && ext.compat[SDKEnvironment.sdkName] != null) {
    return typeof ext.compat[SDKEnvironment.sdkName] === 'string'
      ? satisfies(coerce(SDKEnvironment.version), ext.compat[SDKEnvironment.sdkName] as string)
      : !!ext.compat[SDKEnvironment.sdkName];
  }

  // To gracefully support older extensions, we assume
  // compatibility when the `compat` field is missing.
  return true;
}

/**
 * Generates a network hash of the SDK instance for persisting network specific
 * information on multichain setups
 */
function getNetworkHash(apiKey: string, network?: EthNetworkConfiguration, extConfig?: any) {
  if (!network && !extConfig) {
    return `${apiKey}_eth_mainnet`;
  }
  if (extConfig) {
    return `${apiKey}_${JSON.stringify(extConfig)}`;
  }
  if (network) {
    if (typeof network === 'string') {

      return `${apiKey}_eth_${network}`;
    }
    // Custom network, not necessarily eth.
    return `${apiKey}_${network.rpcUrl}_${network.chainId}_${network.chainType}`;
  }
  return `${apiKey}_unknown`;
}

/**
 * Initializes SDK extensions, checks for platform/version compatiblity issues,
 * then consolidates any global configurations provided by those extensions.
 */
function prepareExtensions(this: SDKBase, options?: MagicSDKAdditionalConfiguration): Record<string, any> {
  const extensions: Extension<string>[] | { [key: string]: Extension<string> } = options?.extensions ?? [];
  const extConfig: any = {};
  const incompatibleExtensions: Extension<string>[] = [];

  if (Array.isArray(extensions)) {
    extensions.forEach((ext) => {
      if (checkExtensionCompat(ext)) {
        ext.init(this);
        if (ext.name || ext.name !== Extension.Anonymous) {
          // Only apply extensions with a known, defined `name` parameter.
          (this as any)[ext.name] = ext;
        }

        if (ext instanceof Extension.Internal) {
          if (!isEmpty(ext.config)) extConfig[ext.name] = ext.config;
        }
      } else {
        incompatibleExtensions.push(ext);
      }
    });
  } else {
    Object.keys(extensions).forEach((name) => {
      if (checkExtensionCompat(extensions[name])) {
        extensions[name].init(this);
        const ext = extensions[name];
        (this as any)[name] = ext;
        if (ext instanceof Extension.Internal) {
          if (!isEmpty(ext.config)) extConfig[extensions[name].name] = ext.config;
        }
      } else {
        incompatibleExtensions.push(extensions[name]);
      }
    });
  }

  if (incompatibleExtensions.length) {
    throw createIncompatibleExtensionsError(incompatibleExtensions);
  }

  return extConfig;
}

export type MagicSDKExtensionsOption<TCustomExtName extends string = string> =
  | Extension<string>[]
  | { [P in TCustomExtName]: Extension<string> };

export interface MagicSDKAdditionalConfiguration<

  TCustomExtName extends string = string,
  TExt extends MagicSDKExtensionsOption<TCustomExtName> = any,
> {
  endpoint?: string;
  locale?: SupportedLocale;
  network?: EthNetworkConfiguration;
  extensions?: TExt;
  testMode?: boolean;
  deferPreload?: boolean;
  useStorageCache?: boolean;
  meta?: any; // Generic field for clients to add metadata
}

export class SDKBase {
  private static readonly __overlays__: Map<string, ViewController> = new Map();

  protected readonly endpoint: string;
  protected readonly parameters: string;
  protected readonly networkHash: string;
  public readonly testMode: boolean;
  public readonly useStorageCache: boolean;

  /**
   * Contains methods for starting a Magic SDK authentication flow.
   */
  public readonly auth: AuthModule;

  /**
   * Contains methods for interacting with user data, checking login
   * status, generating cryptographically-secure ID tokens, and more.
   */
  public readonly user: UserModule;

  /**

 * Contains methods previously under the `ConnectExtension`, including
 * login, show wallet UI, request user info, and more.
 */
public readonly wallet: WalletModule;

/**
 * Contains methods for interacting with NFTs, including purchase.
 */
public readonly nft: NFTModule;

/**
 * Contains a Web3-compliant provider. Pass this module to your Web3/Ethers
 * instance for automatic compatibility with Ethereum methods.
 */
public readonly rpcProvider: RPCProviderModule & AbstractProvider;

/**
 * Creates an instance of Magic SDK.
 */
constructor(public readonly apiKey: string, options?: MagicSDKAdditionalConfiguration) {
  if (!apiKey) throw createMissingApiKeyError();

  if (SDKEnvironment.platform === 'react-native' && options?.endpoint) {
    createReactNativeEndpointConfigurationWarning().log();
  }

  const { defaultEndpoint, version } = SDKEnvironment;
  this.testMode = !!options?.testMode;
  this.useStorageCache = !!options?.useStorageCache;

this.endpoint = createURL(options?.endpoint ?? defaultEndpoint).origin;

// Prepare built-in modules
this.auth = new AuthModule(this);
this.user = new UserModule(this);
this.wallet = new WalletModule(this);
this.nft = new NFTModule(this);
this.rpcProvider = new RPCProviderModule(this) as any;

// Prepare extensions
const extConfig: any = prepareExtensions.call(this, options);

// Encode parameters as base64-JSON
this.parameters = encodeJSON<QueryParameters>({
  API_KEY: this.apiKey,
  DOMAIN_ORIGIN: window.location ? window.location.origin : '',
  ETH_NETWORK: options?.network,
  host: createURL(this.endpoint).host,
  sdk: sdkNameToEnvName[SDKEnvironment.sdkName],
  version,
  ext: isEmpty(extConfig) ? undefined : extConfig,
  locale: options?.locale || 'en_US',
  ...(SDKEnvironment.bundleId ? { bundleId: SDKEnvironment.bundleId } : {}),
  meta: options?.meta,
});

  this.networkHash = getNetworkHash(this.apiKey, options?.network, isEmpty(extConfig) ? undefined : extConfig);
  if (!options?.deferPreload) this.preload();
}

/**
 * Represents the view controller associated with this `MagicSDK` instance.
 */
protected get overlay(): ViewController {
  if (!SDKBase.__overlays__.has(this.parameters)) {
    const controller = new SDKEnvironment.ViewController(this.endpoint, this.parameters, this.networkHash);

    // eslint-disable-next-line @typescript-eslint/ban-ts-comment
    // @ts-ignore - We don't want to expose this method to the user, but we
    // need to invoke in here so that the `ViewController` is ready for use.
    controller.init();

    SDKBase.__overlays__.set(this.parameters, controller);
  }

  return SDKBase.__overlays__.get(this.parameters)!;
}

/**
 * Preloads the Magic view, allowing for faster initial requests in browser
 * environments. Awaiting the returned promise will signal when the Magic view

   * has completed loading and is ready for requests.
   */
  public async preload() {
    await this.overlay.checkIsReadyForRequest;
  }
}

import {
  MagicIncomingWindowMessage,
  MagicOutgoingWindowMessage,
  JsonRpcRequestPayload,
  MagicMessageEvent,
  MagicMessageRequest,
  SDKWarningCode,
} from '@magic-sdk/types';
import { JsonRpcResponse } from './json-rpc';
import { createPromise } from '../util/promise-tools';
import { getItem, setItem } from '../util/storage';
import { createJwt } from '../util/web-crypto';
import { SDKEnvironment } from './sdk-environment';
import { MagicSDKWarning, createModalNotReadyError } from './sdk-exceptions';
import {
  clearDeviceShares,
  encryptAndPersistDeviceShare,
  getDecryptedDeviceShare,
} from '../util/device-share-web-crypto';

interface RemoveEventListenerFunction {
  (): void;
}

interface StandardizedResponse {
  id?: string | number;
  response?: JsonRpcResponse;
}

interface StandardizedMagicRequest {
  msgType: string;
  payload: JsonRpcRequestPayload<any> | JsonRpcRequestPayload<any>[];
  jwt?: string;
  rt?: string;
  deviceShare?: string;
}

/**

 * Get the originating payload from a batch request using the specified `id`.
 */
function getRequestPayloadFromBatch(
  requestPayload: JsonRpcRequestPayload | JsonRpcRequestPayload[],
  id?: string | number | null,
): JsonRpcRequestPayload | undefined {
  return id && Array.isArray(requestPayload)
    ? requestPayload.find((p) => p.id === id)
    : (requestPayload as JsonRpcRequestPayload);
}

/**
 * Ensures the incoming response follows the expected schema and parses for a
 * JSON RPC payload ID.
 */
function standardizeResponse(
  requestPayload: JsonRpcRequestPayload | JsonRpcRequestPayload[],
  event: MagicMessageEvent,
): StandardizedResponse {
  const id = event.data.response?.id;
  const requestPayloadResolved = getRequestPayloadFromBatch(requestPayload, id);

  if (id && requestPayloadResolved) {
    // Build a standardized response object
    const response = new JsonRpcResponse(requestPayloadResolved)
      .applyResult(event.data.response.result)
      .applyError(event.data.response.error);

    return { id, response };
  }

  return {};
}

async function createMagicRequest(
  msgType: string,
  payload: JsonRpcRequestPayload | JsonRpcRequestPayload[],
  networkHash: string,
) {
  const rt = await getItem<string>('rt');
  let jwt;

  // only for webcrypto platforms
  if (SDKEnvironment.platform === 'web') {
    try {
      jwt = (await getItem<string>('jwt')) ?? (await createJwt());
    } catch (e) {
      console.error('webcrypto error', e);
    }
  }

  const request: StandardizedMagicRequest = { msgType, payload };

  if (jwt) {
    request.jwt = jwt;
  }
  if (jwt && rt) {
    request.rt = rt;
  }

  // Grab the device share if it exists for the network
  const decryptedDeviceShare = await getDecryptedDeviceShare(networkHash);
  if (decryptedDeviceShare) {
    request.deviceShare = decryptedDeviceShare;
  }

  return request;
}

async function persistMagicEventRefreshToken(event: MagicMessageEvent) {
  if (!event.data.rt) {
    return;
  }

  await setItem('rt', event.data.rt);
}

export abstract class ViewController {
  public checkIsReadyForRequest: Promise<void>;
  public isReadyForRequest: boolean;
  protected readonly messageHandlers = new Set<(event: MagicMessageEvent) => any>();
  protected isConnectedToInternet = true;

  /**
   * Create an instance of `ViewController`
   *
   * @param endpoint - The URL for the relevant iframe context.
   * @param parameters - The unique, encoded query parameters for the
   * relevant iframe context.
   * @param networkHash - The hash of the network that this sdk instance is connected to
   * for multi-chain scenarios
   */
  constructor(
    protected readonly endpoint: string,
    protected readonly parameters: string,
    protected readonly networkHash: string,
  ) {
    this.checkIsReadyForRequest = this.waitForReady();
    this.isReadyForRequest = false;
    this.listen();
  }

  protected abstract init(): void;
  protected abstract _post(data: MagicMessageRequest): Promise<void>;
  protected abstract hideOverlay(): void;

protected abstract showOverlay(): void;

/**
 * Send a payload to the Magic `<iframe>` for processing and automatically
 * handle the acknowledging follow-up event(s).
 *
 * @param msgType - The type of message to encode with the data.
 * @param payload - The JSON RPC payload to emit via `window.postMessage`.
 */
public async post<ResultType = any>(
  msgType: MagicOutgoingWindowMessage,
  payload: JsonRpcRequestPayload[],
): Promise<JsonRpcResponse<ResultType>[]>;

public async post<ResultType = any>(
  msgType: MagicOutgoingWindowMessage,
  payload: JsonRpcRequestPayload,
): Promise<JsonRpcResponse<ResultType>>;

public async post<ResultType = any>(
  msgType: MagicOutgoingWindowMessage,
  payload: JsonRpcRequestPayload | JsonRpcRequestPayload[],
): Promise<JsonRpcResponse<ResultType> | JsonRpcResponse<ResultType>[]> {
  return createPromise(async (resolve, reject) => {
    if (!this.isConnectedToInternet) {
      const error = createModalNotReadyError();

  reject(error);
}

if (!this.isReadyForRequest) {
  await this.waitForReady();
}

const batchData: JsonRpcResponse[] = [];
const batchIds = Array.isArray(payload) ? payload.map((p) => p.id) : [];
const msg = await createMagicRequest(`${msgType}-${this.parameters}`, payload, this.networkHash);

await this._post(msg);

/**
 * Collect successful RPC responses and resolve.
 */
const acknowledgeResponse = (removeEventListener: RemoveEventListenerFunction) => (event: MagicMessageEvent) => {
  const { id, response } = standardizeResponse(payload, event);
  persistMagicEventRefreshToken(event);
  if (response?.payload.error?.message === 'User denied account access.') {
    clearDeviceShares();
  } else if (event.data.deviceShare) {
    const { deviceShare } = event.data;
    encryptAndPersistDeviceShare(deviceShare, this.networkHash);
  }

      if (id && response && Array.isArray(payload) && batchIds.includes(id)) {
        batchData.push(response);

        // For batch requests, we wait for all responses before resolving.
        if (batchData.length === payload.length) {
          removeEventListener();
          resolve(batchData);
        }
      } else if (id && response && !Array.isArray(payload) && id === payload.id) {
        removeEventListener();
        resolve(response);
      }
    };

    // Listen for and handle responses.
    const removeResponseListener = this.on(
      MagicIncomingWindowMessage.MAGIC_HANDLE_RESPONSE,
      acknowledgeResponse(() => removeResponseListener()),
    );
  });
}

/**
 * Listen for events received with the given `msgType`.
 *
 * @param msgType - The `msgType` encoded with the event data.
 * @param handler - A handler function to execute on each event received.
 * @return A `void` function to remove the attached event.
 */
public on(

  msgType: MagicIncomingWindowMessage,
  handler: (this: Window, event: MagicMessageEvent) => any,
): RemoveEventListenerFunction {
  const boundHandler = handler.bind(window);

  // We cannot effectively cover this function because it never gets reference
  // by value. The functionality of this callback is tested within
  // `initMessageListener`.
  /* istanbul ignore next */
  const listener = (event: MagicMessageEvent) => {
    if (event.data.msgType === `${msgType}-${this.parameters}`) boundHandler(event);
  };

  this.messageHandlers.add(listener);
  return () => this.messageHandlers.delete(listener);
}

private waitForReady() {
  return new Promise<void>((resolve) => {
    const unsubscribe = this.on(MagicIncomingWindowMessage.MAGIC_OVERLAY_READY, () => {
      this.isReadyForRequest = true;
      resolve();
      unsubscribe();
    });

    // We expect the overlay to be ready within 15 seconds.
    // Sometimes the message is not properly processed due to

      // webview issues. In that case, after 15 seconds we consider
      // the overlay ready, to avoid requests hanging forever.
      setTimeout(() => {
        this.isReadyForRequest = true;
        resolve();
        unsubscribe();
      }, 15000);
    });
  }

  /**
   * Listen for messages sent from the underlying Magic `<WebView>`.
   */
  private listen() {
    this.on(MagicIncomingWindowMessage.MAGIC_HIDE_OVERLAY, () => {
      this.hideOverlay();
    });

    this.on(MagicIncomingWindowMessage.MAGIC_SHOW_OVERLAY, () => {
      this.showOverlay();
    });

    this.on(MagicIncomingWindowMessage.MAGIC_SEND_PRODUCT_ANNOUNCEMENT, (event: MagicMessageEvent) => {
      if (event.data.response.result.product_announcement) {
        new MagicSDKWarning(SDKWarningCode.ProductAnnouncement, event.data.response.result.product_announcement).log();
      }
    });
  }
}

