/* Helpers */
private isMobile(): boolean {
  return /Android|webOS|iPhone|iPad|iPod|BlackBerry|BB|PlayBook|IEMobile|Windows Phone|Silk|Opera Mini/i.test(
    navigator.userAgent,
  );
}

private getUserEnv(): UserEnv {
  return {
    env: {

      isMetaMaskInstalled: this.isMetaMaskInstalled(),
      isCoinbaseWalletInstalled: this.isCoinbaseWalletInstalled(),
    },
  };
}

private connectToThirdPartyWallet(provider: Wallets): Promise<any> {
  switch (provider) {
    case Wallets.MetaMask:
      return this.connectToMetaMask();
    case Wallets.CoinbaseWallet:
      return this.connectToCoinbaseWallet();
    default:
      throw new Error(`Invalid provider: ${provider}. Must be one of "metamask" or "coinbase_wallet".`);
  }
}

private isWalletEnabled(wallet: Wallets): Promise<boolean> {
  const isWalletEnabled = createJsonRpcRequestPayload('mc_is_wallet_enabled', [{ wallet }]);
  return this.request<boolean>(isWalletEnabled);
}

/* Triggers connection to wallet, emits success/reject event back to iframe */
private async handleWalletSelected(params: { wallet: Wallets; payloadId: number }) {
  try {
    const address = await this.connectToThirdPartyWallet(params.wallet);

      await setItem(this.localForageKey, params.wallet);
      this.createIntermediaryEvent(Events.WalletConnected as any, params.payloadId as any)(address);
    } catch (error) {
      console.error(error);
      this.createIntermediaryEvent(Events.WalletRejected as any, params.payloadId as any)();
    }
  }

  private async autoConnectIfWalletBrowser(wallet: Wallets): Promise<string[]> {
    let address;
    if (wallet === Wallets.MetaMask) {
      address = await this.getMetaMaskProvider().request({ method: 'eth_requestAccounts' });
    }
    if (wallet === Wallets.CoinbaseWallet) {
      address = await this.getCoinbaseProvider().request({ method: 'eth_requestAccounts' });
    }
    await setItem(this.localForageKey, wallet);
    const autoConnectPayload = createJsonRpcRequestPayload(MagicPayloadMethod.AutoConnect, [{ wallet, address }]);
    const autoConnectRequest = this.request<string[]>(autoConnectPayload);
    return autoConnectRequest;
  }
}

function percentToByte(p: string) {
  return String.fromCharCode(parseInt(p.slice(1), 16));
}

function byteToPercent(b: string) {
  return `%${`00${b.charCodeAt(0).toString(16)}`.slice(-2)}`;
}

/**
 * Encodes a Base64 string. Safe for UTF-8 characters.
 * Original source is from the `universal-base64` NPM package.
 *
 * @source https://github.com/blakeembrey/universal-base64/blob/master/src/browser.ts
 */
function btoaUTF8(str: string): string {
  return btoa(encodeURIComponent(str).replace(/%[0-9A-F]{2}/g, percentToByte));
}

/**
 * Decodes a Base64 string. Safe for UTF-8 characters.
 * Original source is from the `universal-base64` NPM package.
 *
 * @source https://github.com/blakeembrey/universal-base64/blob/master/src/browser.ts
 */
function atobUTF8(str: string): string {
  return decodeURIComponent(Array.from(atob(str), byteToPercent).join(''));
}

/**
 * Given a JSON-serializable object, encode as a Base64 string.
 */
export function encodeJSON<T>(options: T): string {

  return btoaUTF8(JSON.stringify(options));
}

/**
 * Given a Base64 JSON string, decode a JavaScript object.
 */
export function decodeJSON<T>(queryString: string): T {
  return JSON.parse(atobUTF8(queryString));
}

import { getItem, iterate, removeItem, setItem } from './storage';
import { isWebCryptoSupported } from './web-crypto';

export const DEVICE_SHARE_KEY = 'ds';
export const ENCRYPTION_KEY_KEY = 'ek';
export const INITIALIZATION_VECTOR_KEY = 'iv';

const ALGO_NAME = 'AES-GCM'; // for encryption
const ALGO_LENGTH = 256;

export async function clearDeviceShares() {
  const keysToRemove: string[] = [];
  await iterate((value, key, iterationNumber) => {
    if (key.startsWith(`${DEVICE_SHARE_KEY}_`)) {
      keysToRemove.push(key);
    }
  });
  for (const key of keysToRemove) {
    // eslint-disable-next-line no-await-in-loop
    await removeItem(key);
  }
}

function arrayBufferToBase64(buffer: ArrayBuffer) {
  let binary = '';
  const bytes = new Uint8Array(buffer);
  const len = bytes.byteLength;
  for (let i = 0; i < len; i++) {
    binary += String.fromCharCode(bytes[i]);
  }
  return window.btoa(binary);
}

export function base64ToArrayBuffer(base64: string) {
  const binaryString = window.atob(base64);

  const len = binaryString.length;
  const bytes = new Uint8Array(len);
  for (let i = 0; i < len; i++) {
    bytes[i] = binaryString.charCodeAt(i);
  }
  return bytes.buffer;
}

async function getOrCreateInitVector() {
  if (!isWebCryptoSupported()) {
    console.info('webcrypto is not supported');
    return undefined;
  }
  const { crypto } = window;
  const existingIv = (await getItem(INITIALIZATION_VECTOR_KEY)) as Uint8Array;
  if (existingIv) {
    return existingIv;
  }

  const iv = crypto.getRandomValues(new Uint8Array(12)); // 12 bytes for AES-GCM
  return iv;
}

async function getOrCreateEncryptionKey() {
  if (!isWebCryptoSupported()) {
    console.info('webcrypto is not supported');
    return undefined;
  }
  const { subtle } = window.crypto;
  const existingKey = (await getItem(ENCRYPTION_KEY_KEY)) as CryptoKey;
  if (existingKey) {
    return existingKey;
  }

  const key = await subtle.generateKey(
    { name: ALGO_NAME, length: ALGO_LENGTH },
    false, // non-extractable
    ['encrypt', 'decrypt'],
  );

  return key;
}

export async function encryptAndPersistDeviceShare(deviceShareBase64: string, networkHash: string): Promise<void> {
  const iv = await getOrCreateInitVector();
  const encryptionKey = await getOrCreateEncryptionKey();

  if (!iv || !encryptionKey || !deviceShareBase64) {
    return;
  }
  const decodedDeviceShare = base64ToArrayBuffer(deviceShareBase64);

  const { subtle } = window.crypto;

  const encryptedData = await subtle.encrypt(
    {
      name: ALGO_NAME,
      iv,
    },
    encryptionKey,
    decodedDeviceShare,
  );

  // The encrypted device share we store is a base64 encoded string representation
  // of the magic kms encrypted client share encrypted with webcrypto
  const encryptedDeviceShare = arrayBufferToBase64(encryptedData);

  await setItem(`${DEVICE_SHARE_KEY}_${networkHash}`, encryptedDeviceShare);
  await setItem(ENCRYPTION_KEY_KEY, encryptionKey);
  await setItem(INITIALIZATION_VECTOR_KEY, iv);
}

export async function getDecryptedDeviceShare(networkHash: string): Promise<string | undefined> {
  const encryptedDeviceShare = await getItem<string>(`${DEVICE_SHARE_KEY}_${networkHash}`);
  const iv = (await getItem(INITIALIZATION_VECTOR_KEY)) as Uint8Array; // use existing encryption key and initialization vector
  const encryptionKey = (await getItem(ENCRYPTION_KEY_KEY)) as CryptoKey;

  if (!iv || !encryptedDeviceShare || !encryptionKey || !isWebCryptoSupported()) {
    return undefined;
  }

  const { subtle } = window.crypto;
  const ab = await subtle.decrypt({ name: ALGO_NAME, iv }, encryptionKey, base64ToArrayBuffer(encryptedDeviceShare));

  return arrayBufferToBase64(ab);
}

import EventEmitter from 'eventemitter3';

export type EventsDefinition = { [K in string | symbol]: (...args: any[]) => void } | void;

/**
 * An extension of `EventEmitter` (provided by `eventemitter3`) with an adjusted
 * type interface that supports the unique structure of Magic SDK modules.
 */
export class TypedEmitter<Events extends EventsDefinition = void> extends EventEmitter<
  Events extends void ? string | symbol : Events
> {}

type ChainingMethods = 'on' | 'once' | 'addListener' | 'off' | 'removeListener' | 'removeAllListeners';
type NonChainingMethods = 'emit' | 'eventNames' | 'listeners' | 'listenerCount';

type ReplaceReturnType<T extends (...a: any) => any, TNewReturn> = (...a: Parameters<T>) => TNewReturn;

/**
 * Creates a `TypedEmitter` instance and returns helper functions for easily
 * mixing `TypedEmitter` methods into other objects.
 */
export function createTypedEmitter<Events extends EventsDefinition = void>() {
  const emitter = new TypedEmitter<Events>();

  const createChainingEmitterMethod = <T1 extends ChainingMethods, T2>(
    method: T1,
    source: T2,
  ): ReplaceReturnType<TypedEmitter[T1], T2> => {
    return (...args: any[]) => {
      (emitter as any)[method].apply(emitter, args);
      return source;
    };
  };

  const createBoundEmitterMethod = <T extends NonChainingMethods>(method: T): TypedEmitter[T] => {
    return (...args: any[]) => {
      return (emitter as any)[method].apply(emitter, args);
    };
  };

  return {
    emitter,
    createChainingEmitterMethod,
    createBoundEmitterMethod,
  };
}

function* createIntGenerator(): Generator<number, number, void> {
  let index = 0;

  while (true) {
    /* istanbul ignore next */
    if (index < Number.MAX_SAFE_INTEGER) yield ++index;
    else index = 0;
  }
}

const intGenerator = createIntGenerator();

/**
 * Get an integer ID for attaching to a JSON RPC request payload.
 */
export function getPayloadId(): number {
  return intGenerator.next().value;
}

