}

export function createMagicSDKWithExtension(environment: { [P in keyof SDKEnvironment]?: any } = {}, extensions = []) {
  const Ctor = createMagicSDKCtor(environment);
  return new Ctor(TEST_API_KEY, { extensions });
}

