  expect(magic.apiKey).toBe(TEST_API_KEY);
  expect(magic.endpoint).toBe('https://example.com');
  assertEncodedQueryParams(magic.parameters, { host: 'example.com' });
  assertModuleInstanceTypes(magic);
});

