import execa from 'execa';
import isCI from 'is-ci';
import { runAsyncProcess } from '../utils/run-async-process';
import { handleError } from '../utils/handle-script-error';
import { printSeparator } from '../utils/print-separator';
import { getPackages, printPackages, promptForPackage } from '../utils/workspace-helpers';
import { printEnvironment } from '../utils/environment';

async function buildPkgs(PKG: string) {
  printSeparator('Building');

  // We need to limit concurrency in CI to avoid ENOMEM errors.
  const wsrunConcurrency = isCI ? '--serial' : '--stages';

  await execa('yarn', ['wsrun', '-r', wsrunConcurrency, `${process.env.INIT_CWD}/scripts/bin/wsrun/build-package.ts`], {
    stdio: 'inherit',
    env: { PKG },
  })
    .then(() => console.log())
    .catch(handleError('Failed to build libraries.'));
}

async function main() {
  const PKG = await promptForPackage();
  const { packages } = await getPackages(PKG);

  console.log(`\nFound ${packages.length} packages to build:`);
  printPackages(packages);

  console.log(`\nBuilding with the following environment:`);
  printEnvironment();

  await buildPkgs(PKG);
}

runAsyncProcess(main);

#!/usr/bin/env ts-node-script

import execa from 'execa';
import meow from 'meow';
import ora from 'ora';
import chalk from 'chalk';
import { runAsyncProcess } from '../utils/run-async-process';

const spinner = ora();

async function abstractCleanFn(patterns: string[]) {
  await Promise.all(
    patterns.map(async (pattern) => {
      await execa('yarn', ['wsrun:all', '--parallel', '-r', 'rimraf', pattern]);
    }),
  );
}

async function cleanDist() {
  await abstractCleanFn(['dist', 'node_modules/.temp']);
}

async function cleanCache() {
  await abstractCleanFn(['node_modules/.cache']);
}

async function cleanTestArtifacts() {
  await abstractCleanFn(['coverage', '.nyc_output']);
}

async function cleanNodeModules() {
  await Promise.all([execa('rimraf', ['**/node_modules'])]);
}

const helpText = `
  Usage: yarn clean [OPTIONS]

  Options:
    --cache                   Remove cache files.

    --test-artifacts          Remove coverage reports.

    --deps                    Remove node_modules (from all packages).

    -h, --help                Show this message. No cleaning happens if
                              this flag is present.
`;

async function main() {
  const cli = meow(helpText, {
    flags: {
      cache: {
        type: 'boolean',
        default: false,
      },

      testArtifacts: {
        type: 'boolean',
        default: false,
      },

      deps: {
        type: 'boolean',
        default: false,
      },

      help: {
        type: 'boolean',
        default: false,
        alias: 'h',
      },
    },
  });

  if (cli.flags.help) {
    cli.showHelp();
  }

  const enabledCleaners: string[] = [chalk.rgb(0, 255, 255)('dist')];
  const concurrent: (() => Promise<any>)[] = [cleanDist];
  const sequential: (() => Promise<any>)[] = [];

  if (cli.flags.cache) {
    enabledCleaners.push(chalk.rgb(0, 255, 255)('cache'));
    concurrent.push(cleanCache);
  }

  if (cli.flags.testArtifacts) {
    enabledCleaners.push(chalk.rgb(0, 255, 255)('test artifacts'));
    concurrent.push(cleanTestArtifacts);
  }

  if (cli.flags.deps) {
    enabledCleaners.push(chalk.rgb(0, 255, 255)('node_modules'));
    sequential.push(cleanNodeModules);
  }

  spinner.start(`Cleaning ${enabledCleaners.join(', ')}...`);

  const handleError = (err: any) => {
    spinner.fail('Failed!');
    if (err) console.error(err);
    process.exit(1);
  };

  await Promise.all(concurrent.map((p) => p())).catch(handleError);

  await new Promise<void>((resolve, reject) => {
    sequential
      .reduce(async (curr, next) => {
        return curr.then(next);
      }, Promise.resolve())
      .then(() => resolve())
      .catch(reject);
  })
    .then(() => {
      spinner.succeed(`Cleaned ${enabledCleaners.join(', ')}!`);
    })
    .catch(handleError);
}

runAsyncProcess(main);

#!/usr/bin/env ts-node-script

import execa from 'execa';
import chalk from 'chalk';
import { runAsyncProcess } from '../utils/run-async-process';
import { handleError } from '../utils/handle-script-error';
import { printSeparator } from '../utils/print-separator';
import { getPackages, printPackages, promptForPackage } from '../utils/workspace-helpers';
import { printEnvironment } from '../utils/environment';

async function buildDeps(PKG: string) {
  printSeparator('Building Dependencies');

  await execa('yarn', ['wsrun', '-r', '--stages', `${process.env.INIT_CWD}/scripts/bin/wsrun/build-package.ts`], {
    stdio: 'inherit',
    env: { PKG },
  }).catch(handleError('Failed to build dependencies.'));
}

async function startDevServer(PKG: string) {
  printSeparator(chalk`Starting Dev Server for {rgb(0,255,255) ${PKG}}`);

  await execa('yarn', ['wsrun', '--serial', `${process.env.INIT_CWD}/scripts/bin/wsrun/build-package.ts`], {
    stdio: 'inherit',
    env: { PKG, DEV_SERVER: 'true' },
  })

    .then(() => console.log())
    .catch(handleError('Failed to build dependencies.'));
}

async function main() {
  const PKG = await promptForPackage({ allowAll: false });
  const { packages, dependencies } = await getPackages(PKG);

  if (dependencies.length) {
    console.log(`\nFound ${dependencies.length} dependencies to build:`);
    printPackages(packages);

    console.log(`\nBuilding with the following environment:`);
    printEnvironment();

    await buildDeps(dependencies.join(','));
  } else {
    console.log(`\nBuilding with the following environment:`);
    printEnvironment();
  }

  await Promise.all([PKG, ...dependencies].map((pkg) => startDevServer(pkg)));
}

runAsyncProcess(main);

