import { build as esbuild, BuildFailure, BuildResult, Platform, Plugin, Format } from 'esbuild';
import path from 'path';
import fse from 'fs-extra';
import gzipSize from 'gzip-size';
import brotliSize from 'brotli-size';
import prettyBytes from 'pretty-bytes';
import chalk from 'chalk';
import execa from 'execa';
import { environment } from './environment';
import { existsAsync } from './exists-async';

interface ESBuildOptions {
  watch?: boolean;
  target?: Platform;
  format?: Format;
  output?: string;
  sourcemap?: boolean;
  name?: string;
  globals?: Record<string, string>;
  externals?: string[];
}

export async function build(options: ESBuildOptions) {
  if (options.output) {
    try {
      await esbuild({
        bundle: true,
        minify: true,
        watch: options.watch ? { onRebuild: onRebuildFactory(options) } : undefined,
        legalComments: 'none',
        platform: options.target ?? 'browser',
        format: options.format ?? 'cjs',

globalName: options.format === 'iife' ? options.name : undefined,
entryPoints: [await getEntrypoint(options.format)],
sourcemap: options.sourcemap,
outfile: options.output,
tsconfig: 'node_modules/.temp/tsconfig.build.json',
external: options.externals,
loader: { '.ts': 'ts', '.tsx': 'tsx' },
define: Object.fromEntries(
  Object.entries(environment).map(([key, value]) => [`process.env.${key}`, JSON.stringify(value)]),
),
plugins: [...globalsPlugin(options.globals || {})],

// We need this footer because: https://github.com/evanw/esbuild/issues/1182
footer:
  options.format === 'iife'
    ? {
        // This snippet replaces `window.{name}` with
        // `window.{name}.default`, with any additional named exports
        // assigned. Finally, it removes `window.{name}.default`.

                js: `if (${options.name} && ${options.name}.default != null) { ${options.name} = Object.assign(${options.name}.default, ${options.name}); delete ${options.name}.default; }`,
              }
            : undefined,
      });

      await printOutputSizeInfo(options);
    } catch (e) {
      console.error(e);
      throw e;
    }
  }
}

/**
 * Prints the size of the output file(s) produced by ESBuild.
 */
async function printOutputSizeInfo(options: ESBuildOptions) {
  if (options.output) {
    // Log the type and size of the output(s)...
    const outputPath = path.resolve(process.cwd(), options.output);
    const sizeInfo = await getSizeInfo((await fse.readFile(outputPath)).toString(), outputPath);
    console.log(chalk`Built {rgb(0,255,255) ${options.format}} to {gray ${path.dirname(options.output)}}`);
    console.log(sizeInfo);
  }
}

/**
 * Returns a function that can be used to handle rebuild events from ESBuild.
 */
function onRebuildFactory(options: ESBuildOptions) {

  return async (error: BuildFailure | null, result: BuildResult | null) => {
    if (error) {
      console.error(error.message);
    } else {
      await printOutputSizeInfo(options);
    }
  };
}

/**
 * Emits TypeScript typings for the current package.
 */
export async function emitTypes(watch?: boolean) {
  try {
    if (watch) {
      await execa('tsc', ['-w', '-p', 'node_modules/.temp/tsconfig.build.json']);
    } else {
      await execa('tsc', ['-p', 'node_modules/.temp/tsconfig.build.json']);
    }
  } catch (e) {
    console.error(e);
    throw e;
  }
}

/**
 * Resolves the entrypoint file for ESBuild,
 * based on the format and target platform.
 */
async function getEntrypoint(format?: Format) {
  const findEntrypoint = async (indexTarget?: string) => {
    if (format && (await existsAsync(path.resolve(process.cwd(), `./src/index.${indexTarget}.ts`)))) {
      return `src/index.${indexTarget}.ts`;
    }

    return 'src/index.ts';
  };

  switch (format) {
    case 'iife':
      return findEntrypoint('cdn');

    case 'esm':
      return findEntrypoint('es');

    case 'cjs':
    default:
      return findEntrypoint(format);
  }
}

/**
 * During the build step, we create an ephermeral "tsconfig.build.json" file
 * containing config overrides to resolve "packages/.../src" as the "rootDir".
 *
 * Why? This is our workaround to avoid tsconfig project references, which make
 * dependency association difficult to maintain in a repo this size. Using this
 * workaround, we can get the same benefits provided by tsconfig project
 * references using `paths` resolutions. However, this doesn't work when
 * actually building the libraries, so this corrects the behavior at build time.
 */
export async function createTemporaryTSConfigFile() {
  const baseTSConfigPath = path.resolve(__dirname, '../../tsconfig.settings.json');
  const tempTSConfigPath = path.join(process.cwd(), 'node_modules/.temp/tsconfig.build.json');
  const relativeBaseUrl = path.relative(path.dirname(tempTSConfigPath), process.cwd());

  const configuration = {
    extends: path.relative(path.dirname(tempTSConfigPath), baseTSConfigPath),
    compilerOptions: {
      rootDir: path.join(relativeBaseUrl, 'src'),
      noEmit: false,
      emitDeclarationOnly: true,
      baseUrl: undefined,
      declarationDir: path.join(relativeBaseUrl, 'dist/types'),
      // Discard what's configured for "paths" inside the root
      // "tsconfig.settings.json" file.
      paths: {},
    },
    include: [
      path.join(relativeBaseUrl, 'src/**/*.ts'),
      path.join(relativeBaseUrl, 'src/**/*.tsx'),
      path.join(relativeBaseUrl, 'src/**/*.js'),
    ],
  };

  await fse.ensureDir(path.dirname(tempTSConfigPath));
  await fse.writeJSON(tempTSConfigPath, configuration, { encoding: 'utf8' });
}

/**
 * Creates a list of plugins to replace
 * externalized packages with a global variable.
 */
function globalsPlugin(globals: Record<string, string>): Plugin[] {
  return Object.entries(globals).map(([packageName, globalVar]) => {

    const namespace = `globals-plugin:${packageName}`;
    return {
      name: namespace,
      setup(builder) {
        builder.onResolve({ filter: new RegExp(`^${packageName}$`) }, (args) => ({
          path: args.path,
          namespace,
        }));

        builder.onLoad({ filter: /.*/, namespace }, () => {
          const contents = `module.exports = ${globalVar}`;
          return { contents };
        });
      },
    };
  });
}

/**
 * Returns the GZIP and BROTLI sizes of the generated bundle.
 */
export async function getSizeInfo(code: string, filename: string) {
  const raw = code.length < 5000;

  const formatSize = (size: number, type: 'gz' | 'br') => {
    const pretty = raw ? `${size} B` : prettyBytes(size);
    // eslint-disable-next-line no-nested-ternary
    const color = size < 5000 ? chalk.green : size > 40000 ? chalk.red : chalk.yellow;
    return `${color(pretty)}: ${chalk.white(path.basename(filename))}.${type}`;
  };

  const [gzip, brotli] = await Promise.all([gzipSize(code).catch(() => null), brotliSize(code).catch(() => null)]);

  const out = [formatSize(gzip!, 'gz'), formatSize(brotli!, 'br')].join('\n  ');
  return `  ${out}`;
}

import fs from 'fs';

export function existsAsync(input: string) {
  return new Promise((resolve) => {
    fs.exists(input, (exists) => {
      resolve(exists);
    });
  });
}

import ora from 'ora';

type CatchFn<TResult = never> = (reason: any) => TResult | PromiseLike<TResult>;

/**
 * Used to handle errors that occur during script execution.
 *
 * Optionally accepts an Ora spinner instance, which will be set to a failed
 * state with the given `message`. Otherwise, the message is logged to
 * `process.stderr` and the process exits with a non-zero exit code.
 */
export function handleError<TResult = never>(spinner?: ora.Ora, message?: string): CatchFn<TResult>;
export function handleError<TResult = never>(message?: string): CatchFn<TResult>;
export function handleError<TResult = never>(messageOrSpinner?: ora.Ora | string, message?: string): CatchFn<TResult> {
  return (err: any) => {
    if (messageOrSpinner) {
      if (typeof messageOrSpinner === 'string') {
        console.error(messageOrSpinner);
      } else {
        messageOrSpinner.fail(message);
      }
    }

    if (err) console.error(err);
    process.exit(1);
  };
}

export function mockConsole() {
  // Mock console.warn
  Object.defineProperty(console, 'warn', {
    value: jest.fn(),
  });

  // Mock console.info
  Object.defineProperty(console, 'info', {
    value: jest.fn(),
  });

  // Mock console.error
  Object.defineProperty(console, 'error', {
    value: jest.fn(),
  });
}

import chalk from 'chalk';

/**
 * Prints a labeled separator to create a nice visual
 * distinction between sections of script output.
 */
export function printSeparator(label: string) {
  console.log(chalk`\n{dim ❮❮❮} ${label} {dim ❯❯❯}\n`);
}

/**
 * Safely executes the given `fn` as a promise with errors automatically caught.
 */
export function runAsyncProcess(fn: () => void | Promise<void>) {
  Promise.resolve(fn()).catch(() => {
    process.exit(1);
  });
}

import chalk from 'chalk';
import execa from 'execa';
import { prompt } from 'enquirer';
import isCI from 'is-ci';

export interface YarnWorkspace {
  location: string;
  name: string;
  workspaceDependencies: string[];
}

/**
 * Returns metadata for the workspaces in this respository.
 */
export async function getAllWorkspaces(): Promise<YarnWorkspace[]> {
  const subprocess = await execa('yarn', ['workspaces', 'list', '--json', '--verbose']);
  const workspaces = subprocess.stdout
    .split('\n')
    .map((json) => JSON.parse(json))
    .filter((i) => i.name !== 'magic-sdk-monorepo');

  return workspaces;
}

/**
 * Prompts for a package query and returns the result. If the user has already
 * been prompted for a package query, the saved query is returned (attached also
 * to `process.env.PKG`).
 */
export async function promptForPackage(options: { allowAll?: boolean } = {}) {
  if (process.env.PKG) return process.env.PKG;
  if (isCI) return '*';
  const { allowAll = true } = options;

  const packages = (await getAllWorkspaces()).map((i) => i.name);

  const extPkgs = packages.filter((i) => i.includes('@magic-ext'));
  const sdkPkgs = packages
    .filter((i) => i.includes('@magic-sdk') || i === 'magic-sdk')
    // sort `magic-sdk` first
    .sort((i) => (i.startsWith('@') ? 0 : -1));

  const sep = { role: 'separator' };

  const { pkg } = await prompt<any>({
    type: 'autocomplete',
    name: 'pkg',
    message: 'Select a workspace:',
    choices: [allowAll && { name: 'all', value: '*' }, allowAll && sep, ...sdkPkgs, sep, ...extPkgs, sep].filter(
      Boolean,
    ),
    initial: allowAll ? '*' : 'magic-sdk',
  } as any);

  process.env.PKG = pkg;

  return pkg;
}

/**
 * Gets a listing of packages in the workspace
 * (narrowed by the given `pkgQuery`).
 */
export async function getPackages(pkgQuery: string) {
  const workspaces = await getAllWorkspaces();

  const entrypoints = workspaces
    .filter((i) => i.name === pkgQuery || pkgQuery === '*')
    .filter((_, i, arr) => {

    if (pkgQuery !== '*') return true;
    return arr.every((workspace) => {
      return !workspace.workspaceDependencies.includes(workspaces[i].location);
    });
  });

const recursiveDependencyReducer = (acc: YarnWorkspace[], workspace: YarnWorkspace) => {
  const result = [
    ...acc,
    ...workspace.workspaceDependencies.map((i) => workspaces.find((j) => j.location === i)).filter(Boolean),
  ] as YarnWorkspace[];

  const nextDependencies = workspace.workspaceDependencies
    .map((location) => {
      // eslint-disable-next-line @typescript-eslint/no-non-null-asserted-optional-chain
      return workspaces.find((ws) => ws.location === location);
    })
    .filter(Boolean) as YarnWorkspace[];

  if (nextDependencies.length) {
    return nextDependencies.reduce(recursiveDependencyReducer, result);
  }

  return [...new Set(result)];
};

const dependencies = entrypoints.reduce(recursiveDependencyReducer, []);

  const packages = [...dependencies, ...entrypoints].sort((a, b) => {
    if (a.workspaceDependencies.includes(b.location)) return 1;
    if (b.workspaceDependencies.includes(a.location)) return -1;
    return 0;
  });

  return {
    entrypoints: entrypoints.map((i) => i.name),
    dependencies: dependencies.map((i) => i.name),
    packages: packages.map((i) => i.name),
    workspaces: packages,
  };
}

/**
 * Log the given `packages` (a string of package names in the workspace).
 */
export function printPackages(packages: string[]) {
  console.log(
    packages
      .slice()
      .sort()
      .map((cfgPath) => {
        const prefix = chalk`{gray packages/}`;
        return `${prefix}${cfgPath}`;
      })
      .reduce((prev, next, i) => {
        if (i === 0) return chalk`${prev}    {gray -} ${next}`;
        return chalk`${prev}\n    {gray -} ${next}`;
      }, ''),
  );
}

